import { Link, useNavigate } from 'react-router-dom';

export default function Header(props) {

  const navigate = useNavigate();

  let user = JSON.parse(localStorage.getItem("user-info"));

  function logout() {
    localStorage.clear();
    navigate("/")
  }

  return (
    <>
      <div className='block-header row logo'>
       <p>Shopping Cart</p>
      </div>

       <div className='block-header row logo'>

      </div>

      </>
  );
}

